import React from "react";
import {getCampgroundInfo} from "../lib/utils.js"

class Home extends React.Component {
    // constructor
    constructor(props) {
        super(props);
        this.state = {search: "Apache"};
    }

    // input textbox
    handleUpdate(evt) {
        this.setState({search: evt.target.value});
    }

    // handle search from utils
    async handleSearch(evt) {
        const campground = await getCampgroundInfo(this.state.search);
        this.setState({campground});
    }

    // render body
    render() {
        return (
            <div style={{ margin: "auto auto", width: "600px", textAlign: "center"}}>
                <img src="/camping-logo.jpg" alt="campground-logo" className="app-logo"/>
                <h2 className="title">CAMPA'ROUND</h2>
                <p><input type="text" value={this.state.search} onChange={this.handleUpdate.bind(this)}/></p>
                <div className="button-style" onClick={this.handleSearch.bind(this)}>Search</div>
                <style jsx>{`
                    h1,
                    h2,
                    a,
                    p {
                        font-family: 'Roboto';
                    }

                    .title {
                        letter-spacing: 1em;
                        font-size: 2.3em;
                    }

                    .app-logo {
                        width: 250px;
                        height: 200px;
                        border-radius: 50%;
                    }

                    input {
                        margin-top: 5px;
                        margin-bottom: 20px;
                        font-family: Roboto;
                        font-weight: bold;
                        height: 32px;
                        width: 60%;
                        padding: 5px 20px;
                        border-radius: 31px;
                    }

                    .button-style {
                        font-family: Roboto;
                        text-transform: uppercase;
                        margin: auto auto;
                        background-color: black;
                        width: 100px;
                        letter-spacing: .4em;
                        color: white;
                        padding: 10px 30px;
                        border-radius: 31px;
                    }

                    .button-style:hover {
                        background-color: white;
                        border: 2px solid black;
                        padding: 10px 20px;
                    }
                `}</style>
            </div>
        )
    }
}

export default Home;
